INSERT INTO `gc_canned_messages` (`id`, `deletable`, `type`, `name`, `subject`, `content`) VALUES
(3, 0, 'order', 'Order Shipped Notification', 'Your Responsive Respiratory order has shipped!', '<p><img style="background-color: initial; width: 222px;" alt="" src="http://rrioxygen.com/uploads/wysiwyg/images/rri_horiz_logo_copy.jpg"></p><p>Dear {customer_name},</p><p>Thank you for your purchase at {site_name}!</p><p>This message is to inform you that your order PO#  has been shipped.</p><p>We appreciate your business!</p>'),
(6, 0, 'internal', 'Registration', 'Welcome to RESPONSIVE RESPIRATORY INC.', '<center>\n<p>WELCOME TO RESPONSIVE RESPIRATORY INC.</p></center>\n<p>\n	Dear {first_name} {last_name},</p><p>\n	Thanks for registering at RESPONSIVE RESPIRATORY INC. Your participation is appreciated. After registering, you should have been logged in automatically. You may access your account by using the email address this notice was sent to, and the password you signed up with.</p><p>\n	<strong>Name: </strong> {first_name} {last_name}</p><p>\n	<strong>Email: </strong> {email}</p><p>\n	Thanks,</p>'),
(7, 0, 'internal', 'Order Submitted Confirmation', 'Thank you for your order with {site_name}!', '<p>Dear {customer_name},</p><p>Thank you for your order with {site_name}!</p><p>{order_summary}</p>'),
(8, 1, 'order', 'Order Approved', 'Your Responsive Respiratory order has been approved', '<p>Dear {customer_name},</p><p>Thank you for your purchase at {site_name}!</p><p>This message is to inform you that your order ({purchase_order}) has been approved by the Purchasing Manager.</p><p>Thank you!</p>'),
(9, 1, 'order', 'Order Cancelled', 'Your Responsive Respiratory order has been cancelled', '<p>Dear {customer_name},</p><p>Thank you for your purchase at {site_name}!</p><p>This message is to inform you that your order ({purchase_order}) has been cancelled by the Purchasing Manager.</p><p>Please contact your Purchasing Manager for more information.</p>');
